import React, { useState, useEffect } from "react";
import Container from "@mui/material/Container";
import Box from "@mui/material/Box";
import Header from "./components/Header";
import AddTaskForm from "./components/AddTaskForm";
import Tasks from "./components/Tasks";
import Footer from "./components/Footer";
import MenuItem from "@mui/material/MenuItem";
import Select from "@mui/material/Select";
import FilterDropdown from "./components/FilterDropdown";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [isLoaded, setLoaded] = useState(false);
  const [showAddTask, setShowAddTask] = useState(false);
  const [sortStatus, setSortStatus] = useState("");
  const [status, setStatus] = useState("Not Status");
  const [filterStatus, setFilterStatus] = useState("All");

  const handleFilterChange = (event) => {
    setFilterStatus(event.target.value);
    // Add any additional filtering logic here as needed
  };

  // Load tasks from localStorage on initial render
  //useEffect(callback, [variables to listen to] );

  useEffect(() => {
    const savedTasks = JSON.parse(localStorage.getItem("tasks")) || [];
    setTasks(savedTasks);
    setLoaded(true);
  }, []);

  // Save tasks to localStorage whenever tasks array changes
  useEffect(() => {
    if (!isLoaded) return;
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  // Add Task
  const addTask = (task) => {
    const newTask = { id: Date.now(), ...task };
    setTasks([...tasks, newTask]);
  };

  // Edit Task
  const editTask = (updatedTask) => {
    setTasks(
      tasks.map((task) => (task.id === updatedTask.id ? updatedTask : task))
    );
  };

  // Delete Task
  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  // Sort tasks by status
  const sortTasks = (status) => {
    setSortStatus(status);
  };

  const filteredTasks = sortStatus
    ? tasks.filter((task) => task.status === sortStatus)
    : tasks;

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 4, mb: 4, boxShadow: 3, padding: 3, borderRadius: 2 }}>
        <Header
          onAdd={() => setShowAddTask(!showAddTask)}
          showAdd={showAddTask}
        />

        {showAddTask && <AddTaskForm onAdd={addTask} />}

        <Box display="flex" justifyContent="space-between" my={2}>
          {/* <Button
            variant="contained"
            color="primary"
            onClick={() => setShowAddTask(true)}
          >
            Add Task
          </Button> */}
          {/* Filter Dropdown */}
          <FilterDropdown
            filterStatus={filterStatus}
            onFilterChange={handleFilterChange}
          />

          {/* Status Dropdown */}
          <FormControl fullWidth margin="normal">
            <InputLabel id="status-select-label">Select Status</InputLabel>
            <Select
              labelId="status-select-label"
              value={status}
              onChange={handleStatusChange}
            >
              <MenuItem value={"Not Status"}>Not Status</MenuItem>
              <MenuItem value={"In-progress"}>In-progress</MenuItem>
              <MenuItem value={"Review"}>Review</MenuItem>
              <MenuItem value={"Completed"}>Completed</MenuItem>
            </Select>
          </FormControl>
        </Box>

        <Tasks tasks={filteredTasks} onEdit={editTask} onDelete={deleteTask} />
        <Footer />
      </Box>
    </Container>
  );
};

export default App;
