import React from "react";
import Button from "@mui/material/Button";

const TaskStatusButton = ({ currentStatus, onButtonClick }) => {
  const getButtonLabel = () => {
    switch (currentStatus) {
      case "Not Status":
        return "Accept Task";
      case "In-progress":
        return "Mark to Review";
      case "Review":
        return "Mark Completed";
      case "Completed":
        return "Task Completed"; // Final state
      default:
        return "Accept Task";
    }
  };

  // Determine if the task is completed
  const isCompleted = currentStatus === "Completed";

  // Determine button variant based on status
  const buttonVariant = isCompleted ? "contained" : "outlined";
  const buttonColor = isCompleted ? "success" : "primary"; // Set color to success (green) if completed

  return (
    <Button
      variant={buttonVariant}
      onClick={onButtonClick}
      disabled={isCompleted}
      color={buttonColor} // Apply color
      sx={{
        bgcolor: isCompleted ? "green" : undefined, // Set green background if completed
        color: isCompleted ? "white" : undefined, // Change text color to white for better contrast
      }}
    >
      {getButtonLabel()}
    </Button>
  );
};

export default TaskStatusButton;
